import styled from 'styled-components';

const AboutWrapper = styled.div`
  background-color: #000000
  z-index: 1;
  padding: 0 2em 3em 2em;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  .content {
    .header {
      font-size: 1.25rem;
      h1 {
        padding: 0.1em 0;
        font-size: 2.5rem;
        font-weight: 900;
        font-family:cursive;
        letter-spacing: -1.25px;
        height: 400px;



        background-clip: unset;
        background-color: #000000
        -webkit-background-clip: text;

      }
    }
  }
`;

const AboutScreen = () => {
  return (
    <AboutWrapper>
      <div className="content">
        <div className="header">
          <h1 >A2A Cafe</h1> <p>Cafe A2A is a 75 year old French Establishment that has created and fed the best tasting , exquisite pizzas to it's customers since 1954. Paired with the exotic ambience of this fine French-modern setting , Cafe Nui makes it a perfect evening if you're dining with your friends, family or even just by yourself! Avail the luxury of enjoying a delicious slice of French style pizza in your own home ! Quality and exceeding expectations are 2 things we stride to aim while delivering your orders a quickly as we can!</p>
        </div>
      </div>
    </AboutWrapper>
  );
};

export default AboutScreen;
